BEGIN TRANSACTION;
CREATE TABLE "schema_migrations" ("version" varchar NOT NULL);
CREATE TABLE "concerns" ("id" SERIAL PRIMARY KEY  NOT NULL, "name" text, "description" text, "diagnosis" boolean, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "categories" ("id" SERIAL PRIMARY KEY  NOT NULL, "name" text, "description" text, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "categories_concerns" ("id" SERIAL PRIMARY KEY  NOT NULL, "category_id" integer, "concern_id" integer, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "providers" ("id" SERIAL PRIMARY KEY  NOT NULL, "name" text, "organization" text, "address1" text, "address2" text, "city" text, "state" text, "zip" integer, "phone" text, "website" text, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "contact" text);
CREATE TABLE "services" ("id" SERIAL PRIMARY KEY  NOT NULL, "title" text, "description" text, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL, "provider_id" integer);
CREATE TABLE "providers_services" ("id" SERIAL PRIMARY KEY  NOT NULL, "provider_id" integer, "service_id" integer, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "categories_services" ("id" SERIAL PRIMARY KEY  NOT NULL, "category_id" integer, "service_id" integer, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE TABLE "comments" ("id" SERIAL PRIMARY KEY NOT NULL, "commenter" text, "body" text, "service_id" integer, "created_at" timestamp NOT NULL, "updated_at" timestamp NOT NULL);
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_categories_concerns_on_category_id" ON "categories_concerns" ("category_id");
CREATE INDEX "index_categories_concerns_on_concern_id" ON "categories_concerns" ("concern_id");
CREATE INDEX "index_providers_services_on_provider_id" ON "providers_services" ("provider_id");
CREATE INDEX "index_providers_services_on_service_id" ON "providers_services" ("service_id");
CREATE INDEX "index_categories_services_on_category_id" ON "categories_services" ("category_id");
CREATE INDEX "index_categories_services_on_service_id" ON "categories_services" ("service_id");
CREATE INDEX "index_comments_on_service_id" ON "comments" ("service_id");
COMMIT;
